mapper.insert:

	SELECT tu_uid_seq.nextval FROM dual

INSERT INTO tour ( tu_uid
					, us_uid
					, tu_title
					, tu_hits
					, tu_period
					, tu_location
					, tu_with
					, tu_focus
					, tu_write_date
					, tu_start_tour
					, tu_end_tour
					, tu_img_url
					)
			VALUES ( #{tu_uid}
					, #{us_uid}
					, #{tu_title}
					, #{tu_hits}
					, #{tu_period}
					, #{tu_location}
					, #{tu_with}
					, #{tu_focus}
					, sysdate
					, #{tu_start_tour}
					, #{tu_end_tour}
					, #{tu_img_url}
					)


	SELECT memo_uid_seq.nextval FROM dual

INSERT INTO tour_memo ( memo_uid
						, tu_uid
						, tu_day
						, memo_contents
						)
				VALUES ( #{memo_uid}
						, #{tu_uid}
						, #{tu_day}
						, #{memo_contents}
						)


	SELECT lc_uid_seq.nextval FROM dual

INSERT INTO tour_location ( lc_uid
						, place_uid
						, tu_uid
						, tu_day
						)
				VALUES ( #{lc_uid}
						, #{place_uid}
						, #{tu_uid}
						, #{tu_day}
						)

mapper.select:
SELECT * 
FROM(
		SELECT ROWNUM NUM, F.*
		FROM (	
				SELECT  *
				FROM tour 
				ORDER BY tu_write_date DESC
		) F
)
WHERE NUM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE tour
SET tu_title = #{tu_title}
	, tu_period = #{tu_period}
	, tu_location = #{tu_location}
	, tu_with = #{tu_with}
	, tu_focus = #{tu_focus}
	, tu_write_date = sysdate
	, tu_start_tour = #{tu_start_tour}
	, tu_end_tour = #{tu_end_tour}
	, tu_img_url = #{tu_img_url}
WHERE tu_uid = #{tu_uid}

UPDATE tour
SET tu_del_ck = 1
WHERE tu_uid = #{tu_uid}

mapper.select:
SELECT *
FROM tour_like
WHERE tu_uid = #{tu_uid} AND us_uid = #{us_uid}

INSERT INTO tour_like ( tu_like_ck
						, tu_uid
						, us_uid
						)
	VALUES ( 1
			, #{tu_uid} 
			, #{us_uid}
			)

mapper.update:
UPDATE tour
SET tu_hits = tu_hits + 1
WHERE tu_uid = #{tu_uid}
