mapper.delete:
        DELETE FROM PLACE_LIKE 
		WHERE place_uid = #{place_uid} 
		AND us_uid = #{us_uid}

mapper.select:
SELECT count(*) AS likeCnt 
FROM PLACE_LIKE 
WHERE place_uid = #{place_uid}

SELECT count(*) AS "likeChk" 
FROM PLACE_LIKE 
WHERE place_uid = #{place_uid} 
AND us_uid = #{us_uid}
